import React, {useState, useEffect} from 'react';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  NavLink,
} from "react-router-dom";
import { useParams, useHistory } from "react-router-dom";


function App() {
  const [page, setPage] = useState([]);
  const headers = {
    Authorization: 'token 31830d17e83cee43166324aaa35c93d6444e083a'
  };
  function fetchAPI(){
    return fetch(`https://api.github.com/orgs/emberjs/members`, {
      headers: {
        Authorization: 'token 31830d17e83cee43166324aaa35c93d6444e083a',
        Accept: "application/json",
      },
    });
  }

  useEffect(()=>{
    fetchAPI().then(response=>{
      return response.json();
    }).then(response=>{
      setPage(response);
      console.log(response)
    })
  }, [])
  function renderPage(){
    fetchAPI().then(response=>{
      return response.json();
    }).then(response=>{
      setPage(response);
      console.log(response)
    })
  }

  function mapElements(data, handleEach){
    return data.map(element=>{
      handleEach(element);
    })
  }

  function formatElement(element){
    return (
      <>
        <img src={element.avatar_url}></img>
        <div>{element.login}</div>
      </>
    )
  }





  return (
    <>
      <div>
        {mapElements(page, formatElement)}
      </div>

    </>
  );
}

export default App;
