import React, {useState, useEffect} from 'react';
import {fetchFollowers} from "./Api.js"
export default function Page({selectMember, selectRepo}){
    const [page, setPage] = useState([]);
    const [followers, setFollowers] = useState([]);


    function onFollow(elements){
        const new_id = elements.id;
        const element = {
            id : new_id,
            name : elements.login,
        };
        const found = followers.find(element=>{
            return element.id === Number(new_id);
        })
        if (found){
            const newFollowers = followers.filter(element=>{
                return element.id !== Number(new_id);
            })
            setFollowers(newFollowers);
        }else{
            const newFollowers = followers.concat()
        }
    }
    function fetchAPI(){
        return fetch(`https://api.github.com/orgs/emberjs/members`, {
          headers: {
            Authorization: 'token 31830d17e83cee43166324aaa35c93d6444e083a',
            Accept: "application/json",
          },
        });
      }

      useEffect(()=>{
        getFollowers().then(()=>{
            renderPage();
            }
        )

      }, [])

      function getFollowers(){
          return fetchFollowers().then(response=>{
              setFollowers(response);
          })
      }

      function displayFollow(id){
          const found = followers.find(element=>{
              return element.id === Number(id);
          })
          if (found){
              return (<>
                  Unfollow
              </>)
          }
          return <>Follow</>
      }

      function renderPage(){
        fetchAPI().then(response=>{
          return response.json();
        }).then(response=>{
          setPage(response);
        //   console.log(response)
        })
      }

      function mapElements(data, handleEach){
        // console.log(data)
        return data.map(element=>{
          return handleEach(element);
        })
      }

      function formatElement(element){
        return (
          <>
            <img src={element.avatar_url}  onClick={()=>selectMember(element.login)}></img>
            <div onClick={()=>selectMember(element.login)}>{element.login}</div>
            <div>
                <button onClick={()=>selectRepo(element.repos_url)}>Repository</button>
            </div>        
            <div>
                <button onClick={()=>onFollow(element)}>{displayFollow(element.id)}</button>
            </div>            
          </>
        )
      }

      return (<div>
        {mapElements(page, formatElement)}
      </div>)

}
